    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_house_names);

        searchView = findViewById(R.id.hsearch);
        listView = findViewById(R.id.hlistview);

        for(int i=0;i<hnames.length;i++){
            hmodel hmodel = new hmodel(image[0],hnames[i],hgothram[i]);
            hmodelArrayList.add(hmodel);
        }

        MainActivity m = new MainActivity();
        ArrayList<String> gothram_full = new ArrayList<String>();
        Collections.addAll(gothram_full, m.gothram);

        hadapter = new hadapter(this,hmodelArrayList, gothram_full);
        listView.setAdapter(hadapter);

        searchView.setOnQueryTextListener(new SearchView.OnQueryTextListener() {
            @Override
            public boolean onQueryTextSubmit(String query) {
                return false;
            }

            @Override
            public boolean onQueryTextChange(String newText) {

                hadapter.filter(newText);

                return false;
            }
        });

    }
